
public class Que15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count = 5;
		int i=0;
		while(true) {
			System.out.println("***");
			i++;
			if(i == count) {
				break;
			}
			System.out.println("###");
			i++;
			if(i == count) {
				break;
			}
			
		}

	}

}
